mapper.insert:

insert into article (article_id, category_id, title, 
  user_id, issue_time, scan_num, 
  img_path, summary, content
  )
values (#{articleId,jdbcType=INTEGER}, #{categoryId,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, 
  #{userId,jdbcType=INTEGER}, now(), #{scanNum,jdbcType=INTEGER},
  #{imgPath,jdbcType=VARCHAR}, #{summary,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
select a.article_id, title from article a, article_tag atg where a.article_id = atg.article_id
and tag_id = #{tagId}
and a.category_id = #{categoryId}
and issue_time > (select issue_time from article where article_id=#{articleId}) order by issue_time asc limit 1

select a.article_id, title from article a, article_tag atg where a.article_id = atg.article_id
and tag_id = #{tagId}
and a.category_id = #{categoryId}
and issue_time < (select issue_time from article where article_id=#{articleId}) order by issue_time desc limit 1

select count(*) from article a
join article_tag atg join tag on a.article_id = atg.article_id and atg.tag_id = tag.tag_id
and category_id = #{categoryId}
and atg.tag_id = #{tagId}
